mapper.select:

select user_id, phone, password, user_name, register_time, is_actived
from user
where user_id = #{userId,jdbcType=BIGINT}


select user_id, phone, password, user_name, register_time, is_actived
from user
where phone = #{phone,jdbcType=VARCHAR}


select user_id, phone, password, user_name, register_time, is_actived
from user

     and phone = #{phone,jdbcType=VARCHAR}
     and user_id = #{userId,jdbcType=BIGINT}

order BY register_time desc
limit #{start,jdbcType=INTEGER}, #{size,jdbcType=INTEGER}

select count(1)
 from user

      and phone = #{phone,jdbcType=VARCHAR}
      and user_id = #{userId,jdbcType=BIGINT}

SELECT count(1)
FROM device
WHERE user_id= #{userId,jdbcType=BIGINT}

SELECT u1.user_id,u1.phone,u1.`password`,u1.user_name,u1.is_actived,u1.register_time
from user u
INNER JOIN user_friends uf ON uf.user_id=u.user_id
INNER JOIN user u1 on u1.user_id=uf.friend_id
WHERE u.user_id= #{userId,jdbcType=BIGINT}
limit #{start,jdbcType=INTEGER}, #{size,jdbcType=INTEGER}

SELECT device_id as deviceId
from `share`
where user_id= #{userId,jdbcType=BIGINT} and share_id= #{friendId,jdbcType=BIGINT}

SELECT device_id
from `share`
where user_id= #{friendId,jdbcType=BIGINT} and share_id= #{userId,jdbcType=BIGINT}
